import React, { useEffect, useState } from 'react'
import { Map, MapMarker, Roadview, RoadviewMarker } from 'react-kakao-maps-sdk'
import { Col, ListGroupItem, Row } from 'reactstrap'

const RestListItem = (props) => {
    const[lat, setLat] = useState('');
    const[lon, setLon]=useState('');
    const[radius, setRadius]=useState('');

    useEffect(()=>{
      setLat(props.lat);
      setLon(props.lon);
      setRadius(props.radius);
    },[])
  return (
    <ListGroupItem className='py-4 px-4'>
          <Row>
            <Col xs='4'>
              <Roadview // 로드뷰를 표시할 Container
                position={{
                  // 지도의 중심좌표
                  lat: lat,
                  lng: lnt,
                  radius: radius,
                }}
                style={{
                  // 지도의 크기
                  width: "100%",
                  height: "250px",
                  borderRadius:'15px',
                  border:'2px solid #333'
                }}
                
              >
                <RoadviewMarker position={{lat: 37.6820421, lng: 126.7535498,}}>
                  <div style={{ color: "#000" }}>{props.title}</div>
                </RoadviewMarker>
              </Roadview>
            </Col>
            <Col xs='4'>
              <h2>{props.title}</h2>
              <p>{props.sigun}</p>
              <p>{props.tel}</p>
              <p>{props.title_food}</p>
              <p>({props.zip}) {props.address}<br /> {props.address_old}</p>
            </Col>
            <Col xs='4'>
              <Map
                center={{ 
                  lat: 37.6820421, 
                  lng: 126.7535498 
                }}
                style={{ 
                  width: "100%", 
                  height: "250px",
                  borderRadius:'15px',
                  border:'2px solid #333'
                }} 
                level={4}
              >
                <MapMarker position={{ lat: 37.6820421, lng: 126.7535498 }}>
                  <div style={{
                    width:'100%',
                    borderRadius: '10px'
                    }}>{props.title}</div>
                </MapMarker>
              </Map>
            </Col>
          </Row>
        </ListGroupItem>
  )
}

export default RestListItem
